// JavaScript Document

document.getElementById("foundation1").onmouseover = function() {mouseOver6()};
document.getElementById("foundation1").onmouseout = function() {mouseOut6()};

function mouseOver6() {
	document.getElementById("foundation1").src = "images/AdvancedRadianceFoundationBottom.jpg";
}

function mouseOut6() {
	document.getElementById("foundation1").src = "images/AdvancedRadianceFoundationTop.jpg";
}

document.getElementById("foundation2").onmouseover = function() {mouseOver7()};
document.getElementById("foundation2").onmouseout = function() {mouseOut7()};

function mouseOver7() {
	document.getElementById("foundation2").src = "images/AquasmoothCompactFoundationBottom.jpg";
}

function mouseOut7() {
	document.getElementById("foundation2").src = "images/AquasmoothCompactFoundationTop.jpg";
}

